= Release Statistics =
{{groovy}} import org.xwiki.rendering.block.*
def userCountMap = [:] def tableBlock = doc.getDocument().getXDOM().getChildrenByType(TableBlock.class, true).get(0) tableBlock.getChildrenByType(TableRowBlock.class, false).each() { rowBlock -> def cellContentBlock = rowBlock.getChildrenByType(TableCellBlock.class, false).get(1) def users = cellContentBlock.getChildrenByType(LinkBlock.class, false) users.each() { linkBlock -> def user = linkBlock.getReference().getReference(); def count = userCountMap.get(user) if (count == null) { count = 1 } else { count++ } userCountMap.put(user, count) } }
//println "{{chart type='pie' source='inline' params='range:B1-B${userCountMap.size()};series:columns;' title='Release Managers'}}" println "|=Release Manager|=Count" def total = 0 userCountMap = userCountMap.sort {a, b -> b.value <=> a.value} userCountMap.each() { user, count -> println "|[[${user}]]|${count}" total += count } //println "{{/chart}}"
println "" println "Total releases: ${total}" {{/groovy}}
